function filterSwitch(n){let i=$(n).val(),t="";switch(i){case"fullname":t='<label class="uk-form-label">نام و نام خانوادگی را تایپ کنید<\/label><input type="text" name="param" class="uk-input uk-border-rounded" />';break;case"phone":t='<label class="uk-form-label">تلفن همراه را تایپ کنید<\/label><input type="number" name="param" class="uk-input uk-border-rounded" />'}i!="tag"&&$("#append-filter").html(t)}function filter(){let n=$("select[name=filterBy]").val();if(n!=0){preloader();let n=$("#search-modal form").serialize();$.post("/admin/assist",n,function(n){$("#append").html(n)},"html").then(function(){UIkit.modal("#search-modal").hide();preloader()})}else UIkit.notification({message:"لطفا نحوه فیلتر را انتخاب و مقدار آن را پر کنید.",status:"danger",pos:"bottom-center",timeout:6e3}),$("select[name=filterBy]").focus()}function downloadCvFile(){var n=$('#download-form input[name = "assistId"]').val();$("#preloader").fadeIn();$("#download-form").submit();$.post("/admin/download-cv-file",{assistId:n},function(n){n===!1&&UIkit.notification({message:n.msg,status:n.status,pos:"bottom-center",timeout:5e3})},"json").then(function(){$("#preloader").fadeOut()})}function showAssist(n){preloader();$.get("/admin/assist",{assistId:n},function(n){$("#show-assist-modal").html(n)},"html").then(function(){setTimeout(function(){UIkit.modal("#show-assist-modal").show();$("#preloader").hide()},500)})}$("#search-modal form").on("submit",function(n){n.preventDefault();filter()});